// src/pages/Calendario.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Calendario = () => {
  const [date, setDate] = useState(new Date());
  const [selectedLesson, setSelectedLesson] = useState(null);

  // Example lesson dates with details
  const lessons = [
    { date: new Date(2024, 9, 10), title: 'Math Class', description: 'Algebra and Calculus.' },
    { date: new Date(2024, 9, 15), title: 'History Class', description: 'Ancient Civilizations.' },
    { date: new Date(2024, 9, 20), title: 'Physics Class', description: 'Introduction to Mechanics.' },
  ];

  const onChange = (newDate) => {
    setDate(newDate);
    // Check if the selected date matches a lesson date
    const lesson = lessons.find(
      (lesson) =>
        lesson.date.getDate() === newDate.getDate() &&
        lesson.date.getMonth() === newDate.getMonth() &&
        lesson.date.getFullYear() === newDate.getFullYear()
    );
    setSelectedLesson(lesson || null);
  };

  // Function to check if a date is a lesson date
  const isLessonDate = (date) => {
    return lessons.some(
      (lesson) =>
        lesson.date.getDate() === date.getDate() &&
        lesson.date.getMonth() === date.getMonth() &&
        lesson.date.getFullYear() === date.getFullYear()
    );
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
      <div style={{ flex: 2 }}>
        <h1>Calend√°rio</h1>
        <Calendar
          onChange={onChange}
          value={date}
          tileClassName={({ date, view }) =>
            view === 'month' && isLessonDate(date) ? 'lesson-date' : null
          }
        />
        <p>Data selecionada: {date.toDateString()}</p>
      </div>
      <div style={{ flex: 1, marginLeft: '20px', padding: '10px', background: '#f0f0f0', borderRadius: '8px' }}>
        <h2>Detalhes da Aula</h2>
        {selectedLesson ? (
          <div>
            <h3>{selectedLesson.title}</h3>
            <p>{selectedLesson.description}</p>
          </div>
        ) : (
          <p>Nenhuma aula selecionada.</p>
        )}
      </div>
    </div>
  );
};

export default Calendario;
