// pages/Noticias.js
import React, { useState } from 'react';

function Noticias() {
  const news = [
    {
      title: 'Título da Notícia 1',
      date: '13 de Novembro de 2024',
      text: 'Texto resumido da notícia, com informações importantes para os estudantes. Clique para ver mais informações detalhadas sobre a notícia.',
      fullText: 'Aqui está o texto completo da notícia, com todos os detalhes adicionais que aparecem quando o item é expandido.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Título da Notícia 2',
      date: '12 de Novembro de 2024',
      text: 'Outro resumo de notícia com detalhes relevantes para o público estudantil. Clique para ver mais.',
      fullText: 'Texto completo da segunda notícia, com informações detalhadas para os leitores interessados.',
      imageUrl: 'https://via.placeholder.com/150',
    },
  ];

  const [expandedIndex, setExpandedIndex] = useState(null);

  const handleExpand = (index) => {
    setExpandedIndex(index === expandedIndex ? null : index);
  };

  return (
    <div style={styles.container}>
      <h1>Notícias</h1>
      <p>Acompanhe as últimas notícias da universidade.</p>
      <div style={styles.newsContainer}>
        {news.map((item, index) => (
          <div
            key={index}
            style={{
              ...styles.newsItem,
              ...(expandedIndex === index ? styles.expandedNewsItem : {}),
            }}
            onClick={() => handleExpand(index)}
          >
            <div style={styles.imageContainer}>
              <img src={item.imageUrl} alt="Notícia" style={styles.image} />
            </div>
            <div style={styles.textContainer}>
              <h2>{item.title}</h2>
              <p style={styles.date}>{item.date}</p>
              <p>{expandedIndex === index ? item.fullText : item.text}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
  },
  newsContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
    marginTop: '20px',
  },
  newsItem: {
    display: 'flex',
    alignItems: 'flex-start',
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
  },
  expandedNewsItem: {
    backgroundColor: '#f9f9f9',
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.2)',
  },
  imageContainer: {
    minWidth: '150px',
    minHeight: '150px',
    overflow: 'hidden',
    borderRadius: '8px',
    marginRight: '16px',
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  textContainer: {
    flex: '1',
  },
  date: {
    fontSize: '0.9em',
    color: '#666',
    marginBottom: '8px',
  },
};

export default Noticias;
