// pages/Noticias.js
import React from 'react';

function Noticias() {
  const news = [
    {
      title: 'Título da Notícia 1',
      date: '13 de Novembro de 2024',
      text: 'Texto resumido da notícia, com informações importantes para os estudantes.',
      imageUrl: 'https://via.placeholder.com/150', // Replace with your image URL
    },
    {
      title: 'Título da Notícia 2',
      date: '12 de Novembro de 2024',
      text: 'Outro resumo de notícia com detalhes relevantes para o público estudantil.',
      imageUrl: 'https://via.placeholder.com/150', // Replace with your image URL
    },
  ];

  return (
    <div style={styles.container}>
      <h1>Notícias</h1>
      <p>Acompanhe as últimas notícias da universidade.</p>
      <div style={styles.newsContainer}>
        {news.map((item, index) => (
          <div key={index} style={styles.newsItem}>
            <div style={styles.imageContainer}>
              <img src={item.imageUrl} alt="Notícia" style={styles.image} />
            </div>
            <div style={styles.textContainer}>
              <h2>{item.title}</h2>
              <p style={styles.date}>{item.date}</p>
              <p>{item.text}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
  },
  newsContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
    marginTop: '20px',
  },
  newsItem: {
    display: 'flex',
    alignItems: 'flex-start',
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  imageContainer: {
    minWidth: '150px',
    minHeight: '150px',
    overflow: 'hidden',
    borderRadius: '8px',
    marginRight: '16px',
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  textContainer: {
    flex: '1',
  },
  date: {
    fontSize: '0.9em',
    color: '#666',
    marginBottom: '8px',
  },
};

export default Noticias;
