// src/pages/Calendario.js
import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const Calendario = () => {
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Example lesson events
  const events = [
    {
      title: 'INF1039',
      start: new Date(2024, 9, 21, 7, 0), // October 10, 2024, at 9:00 AM
      end: new Date(2024, 9, 21, 9, 0), // October 10, 2024, at 10:30 AM
      description: 'Algebra and Calculus.',
    },
    {
      title: 'INF1039',
      start: new Date(2024, 9, 21, 11, 0), // October 15, 2024, at 11:00 AM
      end: new Date(2024, 9, 21, 12, 30), // October 15, 2024, at 12:30 PM
      description: 'Ancient Civilizations.',
    },
    {
      title: 'Physics Class',
      start: new Date(2024, 9, 23, 14, 0), // October 20, 2024, at 2:00 PM
      end: new Date(2024, 9, 23, 15, 30), // October 20, 2024, at 3:30 PM
      description: 'Introduction to Mechanics.',
    },
  ];

  const onSelectEvent = (event) => {
    setSelectedEvent(event);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
      <div style={{ flex: 2 }}>
        <h1>Calendário Semanal</h1>
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          defaultView="week"
          style={{ height: 600, width: '100%' }}
          onSelectEvent={onSelectEvent}
          min={new Date(2024, 9, 10, 7, 0)} // Limit start time to 7 AM
          max={new Date(2024, 9, 10, 22, 0)} // Limit end time to 10 PM
        />
      </div>
      <div style={{ flex: 1, marginLeft: '20px', padding: '10px', background: '#f0f0f0', borderRadius: '8px' }}>
        <h2>Detalhes da Aula</h2>
        {selectedEvent ? (
          <div>
            <h3>{selectedEvent.title}</h3>
            <p>{selectedEvent.description}</p>
            <p>
              Horário: {moment(selectedEvent.start).format('HH:mm')} - {moment(selectedEvent.end).format('HH:mm')}
            </p>
          </div>
        ) : (
          <p>Nenhuma aula selecionada.</p>
        )}
      </div>
    </div>
  );
};

export default Calendario;
