// src/pages/Calendario.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Calendario = () => {
  const [date, setDate] = useState(new Date());

  // Example lesson dates
  const lessons = [
    new Date(2024, 9, 10), // October 10, 2024
    new Date(2024, 9, 15), 
    new Date(2024, 9, 20), 
  ];

  const onChange = (newDate) => {
    setDate(newDate);
  };

  // Function to check if a date is a lesson date
  const isLessonDate = (date) => {
    return lessons.some(
      (lesson) =>
        lesson.getDate() === date.getDate() &&
        lesson.getMonth() === date.getMonth() &&
        lesson.getFullYear() === date.getFullYear()
    );
  };

  return (
    <div>
      <h1>Calend√°rio</h1>
      <Calendar
        onChange={onChange}
        value={date}
        tileClassName={({ date, view }) =>
          view === 'month' && isLessonDate(date) ? 'lesson-date' : null
        }
      />
      <p>Data selecionada: {date.toDateString()}</p>
    </div>
  );
};

export default Calendario;
